import React, { useEffect } from 'react';
import webgazer from 'webgazer';

const EyeTracking = () => {
  useEffect(() => {
    // Start WebGazer
    webgazer.setGazeListener((data, elapsedTime) => {
      if (data) {
        console.log(data); // Optional: handle gaze data
      }
    }).begin();

    // Optional: hide prediction points
    webgazer.showPredictionPoints(true);

    return () => {
      // SAFELY stop webcam and cleanup
      try {
        webgazer.end(); // Safely ends session
      } catch (error) {
        console.warn("Webgazer cleanup failed:", error);
      }

      // Also try stopping webcam manually
      if (webgazer.params.videoElement && webgazer.params.videoElement.srcObject) {
        const tracks = webgazer.params.videoElement.srcObject.getTracks();
        tracks.forEach((track) => track.stop());
      }
    };
  }, []);

  return (
    <div style={{ padding: '10px', marginTop: '10px' }}>
      <p>üëÅÔ∏è Eye Tracking is running. Focus on your screen to see results.</p>
    </div>
  );
};

export default EyeTracking;
