import React, { useState, useEffect } from 'react';
import '../styles/LexiBloomTitle.css';

function LexiBloomTitle() {
  const [displayedText, setDisplayedText] = useState('');
  const fullText = 'LexiBloom';
  const speed = 150; // speed in ms per character

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setDisplayedText((prev) => prev + fullText[index]);
      index++;
      if (index === fullText.length) clearInterval(interval);
    }, speed);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="typewriter-container">
      <h1 className="typewriter-text">{displayedText}<span className="cursor">|</span></h1>
    </div>
  );
}

export default LexiBloomTitle;
