import React, { useEffect, useState } from 'react';

function EyeTracking() {
  const [coords, setCoords] = useState({ x: null, y: null });
  const [focusLost, setFocusLost] = useState(false);
  let lastSeen = Date.now();

  useEffect(() => {
    const runWebGazer = async () => {
      if (window.webgazer) {
        await window.webgazer.setRegression('ridge')
          .setGazeListener((data, elapsedTime) => {
            if (data) {
              setCoords({ x: Math.round(data.x), y: Math.round(data.y) });
              lastSeen = Date.now();
              setFocusLost(false);
            }
          })
          .begin()
          .showVideoPreview(true)
          .showPredictionPoints(true);
      }

      // Detect attention loss
      const focusCheckInterval = setInterval(() => {
        if (Date.now() - lastSeen > 4000) {
          setFocusLost(true);
        }
      }, 1000);

      return () => clearInterval(focusCheckInterval);
    };

    runWebGazer();

    return () => {
      if (window.webgazer) {
        window.webgazer.end();
      }
    };
  }, []);

  return (
    <div style={{ padding: "1rem", marginTop: "2rem", border: "2px dashed #ccc" }}>
      <h3>üëÅÔ∏è Eye Tracking & Focus Detection</h3>
      <p>Webcam-based attention tracking. Please allow camera access.</p>

      <div style={{
        border: "1px solid #ddd",
        height: "250px",
        textAlign: "center",
        padding: "1rem"
      }}>
        <p>Tracking your gaze here...</p>
        <p>X: {coords.x}, Y: {coords.y}</p>
        {focusLost && (
          <p style={{ color: "red", fontWeight: "bold" }}>
            ‚ö†Ô∏è Focus Lost!
          </p>
        )}
      </div>
    </div>
  );
}

export default EyeTracking;
