import React, { useEffect, useState } from 'react';

function EyeTracking() {
  const [coords, setCoords] = useState({ x: null, y: null });
  const [focusLost, setFocusLost] = useState(false);
  const [webgazerStarted, setWebgazerStarted] = useState(false);
  let lastSeen = Date.now();

  useEffect(() => {
    const runWebGazer = async () => {
      try {
        if (window.webgazer) {
          await window.webgazer.setRegression('ridge')
            .setGazeListener((data, elapsedTime) => {
              if (data) {
                setCoords({ x: Math.round(data.x), y: Math.round(data.y) });
                lastSeen = Date.now();
                setFocusLost(false);
              }
            });

          await window.webgazer.begin();
          setWebgazerStarted(true);

          window.webgazer.showVideoPreview(true);
          window.webgazer.showPredictionPoints(true);
          window.webgazer.showFaceOverlay(true);
        }
      } catch (err) {
        console.error('WebGazer failed to start:', err);
      }
    };

    runWebGazer();

    const interval = setInterval(() => {
      if (Date.now() - lastSeen > 4000) {
        setFocusLost(true);
      }
    }, 1000);

    return () => {
      clearInterval(interval);
      if (window.webgazer && webgazerStarted) {
        try {
          window.webgazer.end();
        } catch (error) {
          console.warn('WebGazer cleanup error:', error.message);
        }
      }

      // Extra: manually stop webcam if any stream exists
      const video = document.querySelector('video');
      if (video && video.srcObject) {
        video.srcObject.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  return (
    <div style={{ border: "2px dashed #ccc", padding: "1rem", marginTop: "1rem", color: "white" }}>
      <h3>üëÅÔ∏è Eye Tracking & Focus Detection</h3>
      <p>Webcam-based attention tracking. Please allow camera access.</p>
      <div style={{ border: "1px solid #ddd", padding: "1rem", marginTop: "1rem", color: "white" }}>
        <p style={{ fontWeight: 'bold' }}>Tracking your gaze here...</p>
        <p>X: {coords.x ?? '--'}, Y: {coords.y ?? '--'}</p>
        {focusLost && (
          <p style={{ color: "red", fontWeight: "bold" }}>
            ‚ö†Ô∏è Focus Lost!
          </p>
        )}
      </div>
    </div>
  );
}

export default EyeTracking;
