import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/home.css'; // adjust as needed

const Home = () => {
  const navigate = useNavigate();

  const goToFeature = (sectionId) => {
    navigate(`/features#${sectionId}`);
  };

  return (
    <div className="home-page">
      <header className="home-header">
        <h1 className="home-title">Lexibloom</h1>
      </header>

      <div className="feature-card-container">
        <div className="feature-card" onClick={() => goToFeature('dyslexia-reader')}>
          <div className="feature-icon">üëÅÔ∏è</div>
          <h2 className="feature-title">Dyslexia-Friendly Reader</h2>
          <p className="feature-description">
            Specially designed reading interface with customizable fonts, spacing, and colors to reduce
            reading difficulties and improve comprehension.
          </p>
        </div>

        <div className="feature-card" onClick={() => goToFeature('text-simplifier')}>
          <div className="feature-icon">‚ú®</div>
          <h2 className="feature-title">Text Simplifier</h2>
          <p className="feature-description">
            AI-powered text simplification that breaks down complex sentences into clearer language while
            maintaining the original meaning.
          </p>
        </div>

        <div className="feature-card" onClick={() => goToFeature('text-to-speech')}>
          <div className="feature-icon">üîä</div>
          <h2 className="feature-title">Text-to-Speech</h2>
          <p className="feature-description">
            Natural-sounding voice synthesis that reads content aloud with adjustable speed and tone.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Home;
