import React, { useState, useEffect } from 'react';
import '../styles/LexiBloomTitle.css';

function LexiBloomTitle({ text = "LexiBloom", speed = 150 }) {
  const [displayedText, setDisplayedText] = useState('');

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      if (index < text.length) {
        setDisplayedText((prev) => prev + text.charAt(index));
        index++;
      } else {
        clearInterval(interval); // stop when finished
      }
    }, speed);

    return () => clearInterval(interval);
  }, [text, speed]);

  return (
    <div className="typewriter-container">
      <h1 className="typewriter-text">{displayedText}<span className="cursor">|</span></h1>
    </div>
  );
}

export default LexiBloomTitle;
